import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class extends Component {
  goToPrev= async()=>{
    let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=c9bf42d72c0f4ee8815803c65dd02d22&page=${this.state.page-1}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({page:this.state.page-1, articles: parsedData.articles });
  }
  goToNext= async()=>{
    let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=c9bf42d72c0f4ee8815803c65dd02d22&page=${this.state.page+1}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({page:this.state.page+1, articles: parsedData.articles });
  }


  constructor() {
    super();
    this.state = {
      //here inside the state;use articles, paste the example response code copied from newsapi in a variable articles.
      articles: [],
      loading: false
    }
  }
  // async componentDidMount() {
  //   let url = "https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=c9bf42d72c0f4ee8815803c65dd02d22";
  //   let data = await fetch(url);
  //   let parsedData = await data.json();
  //   this.setState({ articles: parsedData.articles });
  // }
  render() {
    return (
      <div className="container my3">
        <h2>Top HeadLines</h2>
        <div className="row">
          {this.state.articles.map((element) => {
            return <div className="col-md-4 my-3">
              <NewsItem title={element.title === undefined || element.title === null ? "" : element.title} description={element.description === undefined || element.description === null ? "" : element.description} imgurl={element.urlToImage === undefined || element.urlToImage === null ? "" : element.urlToImage} newsurl={element.url === undefined || element.url === null ? "https://google.com" : element.url} />
            </div>
          })}
          <div className="container d-flex justify-content-between">
          <button disabled={this.state.page<=1} type="button" class="btn btn-dark"onClick={this.goToPrev}>&larr; Prev Page</button>
          <button type="button" class="btn btn-dark" onClick={this.goToNext}>Next Page &rarr;</button>
        </div>
        </div>
      </div>
    )
  }
}
